  while (a == b) {
    // empty!
  }
}


public class Foo {
    public void bar() {
        try {
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}


public class Foo {
    public void bar() {
        try {
            int x=2;
        } finally {
            // empty!
